// Funci√≥n para iniciar el temporizador y la barra de progreso
function iniciarTemporizador() {
    // Redirigir al enlace de monetag en una nueva pesta√±a
    window.open("https://phoampor.top/4/8200231", "_blank");

    document.getElementById("btnVerAd").disabled = true; // Deshabilitar bot√≥n para evitar doble click
    let segundos = 15;
    let progreso = 0;
    document.getElementById("temporizador").style.display = "block";
    const countdown = document.getElementById("countdown");
    const progresoBarra = document.getElementById("barraProgreso");
    document
        .getElementById("barraProgresoContainer")
        .classList.remove("hidden");

    const intervalo = setInterval(() => {
        segundos--;
        progreso += 100 / 15;
        countdown.textContent = segundos;
        progresoBarra.style.width = `${progreso}%`;

        if (segundos <= 0) {
            clearInterval(intervalo);
            saldo += 0.1;
            actualizarSaldo();
            mostrarMensaje(
                "‚úÖ ¬°Has recibido 0.10‚ÇΩ por ver el anuncio!",
            );
            document.getElementById("modal").style.display = "none"; // Ocultar ventana
            document
                .getElementById("barraProgresoContainer")
                .classList.add("hidden");
            desactivarBotonVerAnuncio();

            // Registrar la vista
            vistasHoy++;
            localStorage.setItem("vistasHoy", vistasHoy);

            if (vistasHoy >= maxVistasPorDia) {
                mostrarMensaje(
                    "üö´ Has alcanzado el l√≠mite de 20 anuncios por hoy. Vuelve en 24 horas.",
                );
                document.getElementById("btnVerAnuncio").disabled =
                    true;
                setTimeout(() => {
                    vistasHoy = 0;
                    localStorage.setItem("vistasHoy", vistasHoy);
                    document.getElementById(
                        "btnVerAnuncio",
                    ).disabled = false;
                }, tiempoEsperaEntreVistas);
            }
        }
    }, 1000);
}

// Desactivar el bot√≥n "Ver Anuncio" durante 2 minutos
function desactivarBotonVerAnuncio() {
    const boton = document.getElementById("btnVerAnuncio");
    boton.disabled = true;
    let segundos = tiempoDeDesactivacion / 1000;
    const textoOriginal = "Ver anuncio";
    const intervalo = setInterval(() => {
        boton.innerText = `Espera ${segundos}s`;
        segundos--;
        if (segundos < 0) {
            clearInterval(intervalo);
            boton.disabled = false;
            boton.innerHTML = `üì∫${textoOriginal}`;
        }
    }, 1000);
}

// Reclamar Bono Diario
function reclamarBonoDiario() {
    const ultimoBono = localStorage.getItem("ultimoBono");
    const ahora = Date.now();

    if (
        ultimoBono &&
        ahora - parseInt(ultimoBono) < 24 * 60 * 60 * 1000
    ) {
        const horasRestantes = Math.ceil(
            (24 * 60 * 60 * 1000 - (ahora - parseInt(ultimoBono))) /
                3600000,
        );
        mostrarMensaje(
            `‚è≥ Ya has reclamado tu bono diario. Vuelve en ${horasRestantes} horas.`,
        );
        return;
    }

    saldo += 2;
    actualizarSaldo();
    mostrarMensaje("üéâ ¬°Has recibido 2‚ÇΩ de bono diario!");
    localStorage.setItem("ultimoBono", ahora);
}

// Copiar enlace de referido
function copiarEnlace() {
    const enlaceInput = document.getElementById("enlaceReferido");
    enlaceInput.select();
    document.execCommand("copy");
    mostrarMensaje("‚úÖ Enlace copiado al portapapeles.");
}

