// Simulaci√≥n de retiro
function solicitarRetiro() {
    if (saldo < 35) {
        mostrarMensaje(
            "üí° Necesitas m√≠nimo 35‚ÇΩ para solicitar un retiro.",
        );
    } else {
        saldo -= 35;
        actualizarSaldo();
        mostrarMensaje(
            "‚úÖ Solicitud de retiro enviada correctamente.",
        );
    }
}

